mapper.select:
select user_id, name, password, phone, email, address,
	reg_date, update_date 
from (
	select row_number() over (order by user_id) as seq,
		user_id, name, password, phone, email, address,
		reg_date, update_date
	from member
) where seq between #{start} and #{end}

mapper.update:
update member set
	password = #{password},
	salt = #{salt}
where user_id=#{userId}
